import React, { Component } from "react";

class Todo extends Component {
  state = {};
  render() {
    return (
      <div>
        <h3
          className="text-dark text-center p-1 bg-light border-bottom"
          style={{
            textDecoration: this.props.todo.complete ? "line-through" : "none",
          }}
          onClick={this.props.onClick}
        >
          {this.props.todo.title}
        </h3>
        <i
          className="far fa-times-circle fa-sm float-left m-1 text-danger"
          onClick={this.props.onDelete}
        ></i>

        <input type="checkbox" className="m-2 float-right" />
      </div>
    );
  }
}

export default Todo;
