import React, { Component } from "react";

class Todo extends Component {
  state = {};
  render() {
    return (
      <div>
        <div className="form-check-inline">
          <i
            className="far fa-times-circle fa-lg float-left m-3 text-danger"
            style={{ cursor: "pointer" }}
            onClick={this.props.onDelete}
          ></i>
          <h3
            className="text-dark text-center p-1 bg-light border-bottom"
            style={{
              textDecoration: this.props.todo.complete
                ? "line-through"
                : "none",
            }}
            onClick={this.props.onClick}
          >
            {this.props.todo.title}
          </h3>
          <input
            type="checkbox"
            className="m-2 float-right"
            //style={{ backgroundColor: "red", border: "1px solid red" }}
          />
        </div>
      </div>
    );
  }
}

export default Todo;
