import React, { Component } from "react";

class Todo extends Component {
  state = {};
  render() {
    console.log({
      textDecoration: this.props.complete ? "line-through" : "none",
    });
    return (
      <h3
        className="text-dark text-center p-1 bg-light border-bottom"
        style={{
          textDecoration: this.props.complete ? "line-through" : "none",
          //textDecoration: "line-through",
        }}
      >
        <i className="far fa-times-circle fa-sm float-left m-1 text-danger"></i>
        {this.props.todo.title}
        <input type="checkbox" className="m-2 float-right" />
      </h3>
    );
  }
}

export default Todo;
