import React, { Component } from "react";

const Context = React.createContext();
export class Provider extends Component {
  state = {
    todos: [
      {
        id: 1,
        title: "check emails",
        complete: true,
      },
      {
        id: 2,
        title: "check voicemail",
        complete: false,
      },
      {
        id: 3,
        title: "check calls",
        complete: false,
      },
    ],
  };
  render() {
    return (
      <Context.Provider value={this.state}>
        {this.props.children}
      </Context.Provider>
    );
  }
}

export const Consumer = Context.Consumer;
