import React, { Component, useContext } from "react";
import Todo from "./Todo";
import { Context } from "../context";

const Todos = () => {
  const [todos, setTodos] = useContext(Context);

  return (
    <div>
      {(value) => {
        const { todos } = value;
        return todos.map((todo) => <Todo todo={todo} key={todo.id} />);
      }}
    </div>
  );
};

export default Todos;
